    private void btnAdcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAdcActionPerformed
        Produto p = new Produto(txtNome.getText(), Double.parseDouble(txtPreco.getText()),
                Integer.parseInt(txtQuant.getText()));
        Gp g = new Gp();
        txtArea.setText(p.toString());

        

        g.addProduto(p.toString());

        g.ExbirP();
        g.listarProdutos();
    }//GEN-LAST:event_btnAdcActionPerformed

